import React, { useState } from 'react';
import Button from '@material-ui/core/Button';


const Professional = () => {
 const [edu,setEdu] = useState(false);
 const [school,setSchool] = useState(false);
 const [collage,setCollage] = useState(false);

 const addEducation = () => {
   setEdu(true);
 }

 const hide_edu = () => {
   setEdu(false);
 }

 const addSchool = () => {
   setSchool(true);
 }

 const hide_sch = () => {
  setSchool(false);
  setEdu(false);
}

const addCollage = () => {
  setCollage(true);
}

const hide_clg = () => {
 setCollage(false);
 setEdu(false);
}

    return (
              <>
         
                 <div className="main_professional">
                <form class="was-validated">
                <h1>Add Profesaional Details :</h1>
  <div class="mb-3">
    <label for="validationTextarea" class="form-label my-3">Profession Summary</label>
    <textarea class="form-control is-invalid" id="validationTextarea" placeholder="Teacher for 10+ years and..." required></textarea>
  </div>
</form>
    
    <Button className="education" onClick={addEducation} onDoubleClick={hide_edu}>Add Education</Button> <br/>
   { edu ? <Button className="education" onClick={addSchool} onDoubleClick={hide_sch}>Add School</Button> : null }
   
 <div class="gy-5">
<form class="row g-3 gy-3">
  <div class="col-md-6">
   {school ? <label for="inputEmail4" class="form-label">Email</label> : null }
   {school ? <input type="email" class="form-control" id="inputEmail4"/> : null }
  </div>
  <div class="col-md-6">
  {school ?<label for="inputPassword4" class="form-label">Password</label>  : null }
    {school ? <input type="password" class="form-control" id="inputPassword4"/> : null }
  </div>
  <div class="col-12">
  {school ? <label for="inputAddress" class="form-label">Address</label>: null }
  {school ? <input type="text" class="form-control" id="inputAddress" placeholder="1234 Main St"/>: null }
  </div>
  <div class="col-12">
  {school ? <label for="inputAddress2" class="form-label">Address 2</label> : null }
  {school ? <input type="text" class="form-control" id="inputAddress2" placeholder="Apartment, studio, or floor"/> : null }
  </div>
  <div class="col-md-6">
  {school ? <label for="inputCity" class="form-label">City</label> : null }
  {school ? <input type="text" class="form-control" id="inputCity"/> : null }
  </div>
  
  <div class="col-md-2">
  {school ?<label for="inputZip" class="form-label">Zip</label> : null }
  {school ? <input type="text" class="form-control" id="inputZip"/> : null }
  </div>
  <div class="col-12">
    <div class="form-check">
    {school ? <input class="form-check-input" type="checkbox" id="gridCheck"/> : null }
    {school ? <label class="form-check-label" for="gridCheck">
        Check me out
      </label>  : null }
    </div>
  </div>
  <div class="col-12">
  {school ? <button type="submit" class="btn btn-primary" onClick={hide_sch}>+Add</button> : null }
  
  </div>
</form>
</div>



{ edu ? <Button className="education" onClick={addCollage} onDoubleClick={hide_clg}>Add collage</Button> : null }

 <div class="gy-5">
<form class="row g-3 gy-3">

  <div class="col-md-6">
   {collage ? <label for="inputEmail4" class="form-label">Email</label> : null }
   {collage ? <input type="email" class="form-control" id="inputEmail4"/> : null }
  </div>
  <div class="col-md-6">
  {collage ?<label for="inputPassword4" class="form-label">Password</label>  : null }
    {collage ? <input type="password" class="form-control" id="inputPassword4"/> : null }
  </div>
  <div class="col-12">
  {collage ? <label for="inputAddress" class="form-label">Address</label>: null }
  {collage ? <input type="text" class="form-control" id="inputAddress" placeholder="1234 Main St"/>: null }
  </div>
  <div class="col-12">
  {collage ? <label for="inputAddress2" class="form-label">Address 2</label> : null }
  {collage ? <input type="text" class="form-control" id="inputAddress2" placeholder="Apartment, studio, or floor"/> : null }
  </div>
  <div class="col-md-6">
  {collage ? <label for="inputCity" class="form-label">City</label> : null }
  {collage ? <input type="text" class="form-control" id="inputCity"/> : null }
  </div>
  
  <div class="col-md-2">
  {collage ?<label for="inputZip" class="form-label">Zip</label> : null }
  {collage ? <input type="text" class="form-control" id="inputZip"/> : null }
  </div>
  <div class="col-12">
    <div class="form-check">
    {collage ? <input class="form-check-input" type="checkbox" id="gridCheck"/> : null }
    {collage ? <label class="form-check-label" for="gridCheck">
        Check me out
      </label>  : null }
    </div>
  </div>
  <div class="col-12">
  {collage ? <button type="submit" class="btn btn-primary" onClick={hide_edu}>+Add</button> : null }
  
  </div>
</form>
</div>
</div>

 
 
              </>

    );
}

export default Professional;
